//In case you want to start tor with the local config (ie, the one generated by example.js) and then connect to the control port
var thsBuilder = require('./index');

var ths = thsBuilder(undefined, undefined, undefined, console.error, console.log, function(message){console.log('ctrl message: ' + message);});
ths.start(true, function(){
	console.log('Tor has been started');
	console.log('Control password: ' + ths.controlPass());
});
